definition.script:
pipeline {
    agent any

    environment {
        WORKDIR = "${env.WORKSPACE}/gui_ike_debugger_reforked"
        SOURCEDIR = "/home/lukas/lvs-dev/gui_ike_debugger_reforked"
        WORKSUBDIR = "${WORKDIR}/gui_ike_debugger_reforked"
    }

    stages {
        stage('Prepare Environment') {
            steps {
                sh '''
                    cd "${SOURCEDIR}"
                    git pull --no-rebase
                    rm -rf "${WORKDIR}"
                    mkdir -p "${WORKDIR}"
                    cp -r "${SOURCEDIR}"/ "${WORKDIR}"
                '''
            }
        }

        stage('Setup Python Virtual Environment') {
            steps {
                dir("${env.WORKSUBDIR}") {
                    sh '''
                    # Install dependencies in virtual environment
                    python3 -m venv venv
                    source venv/bin/activate
                    pip install -r requirements.txt
                ''' 

                }
            }
        }
        stage('Generate new JSON files') {
            steps {
                dir("${env.WORKSUBDIR}") {
                    sh '''
                        # Activate virtual environment
                        source venv/bin/activate
                        python -m src.tests.generate_output
                    '''
                }
            }
        }

        stage('Update JSON_tests.py file') {
            steps {
                dir("${env.WORKSUBDIR}") {
                    sh '''
                        # Activate virtual environment
                        source venv/bin/activate
                        python -m src.tests.gen_test_strings
                    '''
                }
            }
        }
        stage("Push updated files to Lukas's Github") {
            steps {
                dir("${env.WORKSUBDIR}") {
                    sh '''
                    git config user.name "Jenkins Lukas ðŸ¤–"
                    git config user.email "lstruth@fortinet.com"
                    git add .
                    git commit -m "Jenkins committed updated json test files on $(date)"
                    git push
                    '''
                }
            }
        }
    }
}
